package day3;

public class Incrementoperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int a = 10;
		a = a + 1;
		// a++;
		System.out.println(a);

		// post increment
		/*
		 * int a=10; int res=a++; System.out.println(res); //10
		 */

		// pre increment
		/*
		 * int a=10; int res=++a; System.out.println(res); //11
		 */

	}

}
