package day4;

public class Evenoroddnumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// eg1
		int num = 10;

		//// here we are checking the remainder by using modular division(%) if it is
		//// equal to 0 then

		if (num % 2 == 0) {

			System.out.println("The Number is even Number ");

		}

		else {

			System.out.println("The Number is Odd Number");
		}

		// eg1
		int num1 = 11;

		//// here we are checking the remainder by using modular division(%) if it is
		//// equal to 0 then

		if (num1 % 2 == 0) {

			System.out.println("The Number1 is even Number ");

		}

		else {

			System.out.println("The Number1 is Odd Number");
		}

	}

}
