package day5;

public class Whilevsdowhileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		// While 
		int i=10;
		
		while(i<=5) {
			
			System.out.println(i);
			i++;
		}
		
		//do while loop
		
		int a=10;
		
		do {
			System.out.println(a);  // 10
			a++;
		}while(a<=5);
		
		

	}

}
