package day5;

public class dowhileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//example1 Print the numbers 1 to 10 using do while loop
		int i=1;
		
		do {
			System.out.println(i);
			i++;
			
		}while(i<=10);
		
		
		//example2 Print the numbers 10 to 1 using do while loop
		
		System.out.println("Printing the No in reverse");
		int a=10;
		
		do {
			
			System.out.println(a);
			a--;
		}while(a>=1);
	}

}
